import React, { useState } from 'react';
import { Chart as ChartJs, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, ArcElement } from 'chart.js';
import { Line } from 'react-chartjs-2';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import { dateFormat } from '../../utils/dateFormat';
import { parseISO, startOfMonth, endOfMonth, startOfWeek, endOfWeek, startOfYear, endOfYear, isWithinInterval, format } from 'date-fns';

ChartJs.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
);

function Chart() {
    const { incomes, expenses } = useGlobalContext();
    const [timeframe, setTimeframe] = useState('monthly');

    const now = new Date();

    const filterItemsByDateRange = (items, startDate, endDate) => {
        return items.filter(item => {
            const itemDate = parseISO(item.date);
            return isWithinInterval(itemDate, { start: startDate, end: endDate });
        });
    };

    const aggregateData = (items, period) => {
        const periods = {
            monthly: { start: startOfMonth(now), end: endOfMonth(now) },
            weekly: { start: startOfWeek(now), end: endOfWeek(now) },
            yearly: { start: startOfYear(now), end: endOfYear(now) },
        };

        const { start, end } = periods[period];
        const filteredItems = filterItemsByDateRange(items, start, end);

        return filteredItems.map(item => ({
            ...item,
            formattedDate: format(parseISO(item.date), 'dd/MM/yyyy')
        }));
    };

    const aggregatedIncomes = aggregateData(incomes, timeframe);
    const aggregatedExpenses = aggregateData(expenses, timeframe);

    const data = {
        labels: aggregatedIncomes.map(inc => inc.formattedDate),
        datasets: [
            {
                label: 'Income',
                data: aggregatedIncomes.map(income => income.amount),
                borderColor: 'green',
                backgroundColor: 'rgba(0, 255, 0, 0.5)',
                tension: 0.2,
                fill: true,
            },
            {
                label: 'Expenses',
                data: aggregatedExpenses.map(expense => expense.amount),
                borderColor: 'red',
                backgroundColor: 'rgba(255, 0, 0, 0.5)',
                tension: 0.2,
                fill: true,
            }
        ]
    };

    const options = {
        responsive: true,
        plugins: {
            legend: {
                position: 'bottom',
                labels: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            },
            tooltip: {
                mode: 'index',
                intersect: false,
                bodyFont: {
                    family: 'Segoe UI',
                },
                titleFont: {
                    family: 'Segoe UI',
                },
            },
        },
        scales: {
            x: {
                display: true,
                title: {
                    display: false,
                },
                ticks: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            },
            y: {
                display: true,
                title: {
                    display: false,
                },
                beginAtZero: true,
                ticks: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            }
        }
    };

    return (
        <ChartStyled>
            <h2>Analytics</h2>
            <small>Track your expenses and income.</small>
            <div className="header">
                <div className="timeframe-selector">
                    <label htmlFor="timeframe">Select Timeframe: </label>
                    <select id="timeframe" value={timeframe} onChange={(e) => setTimeframe(e.target.value)}>
                        <option value="weekly">Weekly</option>
                        <option value="monthly">Monthly</option>
                        <option value="yearly">Yearly</option>
                    </select>
                </div>
            </div>
            <Line options={options} data={data} />
        </ChartStyled>
    );
}

const ChartStyled = styled.div`
    background: #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0);
    padding: 1rem;
    border-radius: 20px;
    height: 100%;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;

    .header {
        display: flex;
        flex-direction: column;
        align-items: center;
        margin-bottom: 1rem;

        h2 {
            margin: 0;
            font-size: 1.5rem;
            color: #333;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
        }

        small {
            font-size: 1rem;
            color: #696969;
            margin-bottom: 1rem;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
        }
    }

    .timeframe-selector {
        display: flex;
        align-items: center;
        margin-bottom: 0.5rem;

        label {
            margin-right: 1rem;
            font-size: 1rem;
            color: #696969;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
        }

        select {
            padding: 0.5rem;
            border: 1px solid #ddd;
            border-radius: 5px;
            font-size: 1rem;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            color: #696969;
        }
    }
`;

export default Chart;
