import React, { useState } from "react";
import styled from "styled-components";
import Button from "../Button/Button";
import axios from "axios";
import Cookies from "js-cookie";
import Pic1 from "../../img/pic1.png";

const Authentication = () => {
  const [isLogin, setIsLoginPage] = useState(true);
  const [error, setError] = useState("");

  const defaultFormFields = {
    name: "",
    email: "",
    password: "",
    cpassword: "",
  };

  const BASE_URL = "http://localhost:5000/api/v1";
  const [formFields, setFormFields] = useState(defaultFormFields);

  const login = () => {
    axios.post(`${BASE_URL}/login`, formFields)
      .then((res) => {
        alert(res.data.message);
        Cookies.set("et-auth-name", res.data.currentUser.name, { expires: 7 });
        window.location.reload();
      })
      .catch((err) => setError(err.response.data.message));
  };

  const register = () => {
    axios.post(`${BASE_URL}/register`, formFields)
      .then((res) => {
        alert(res.data.message);
        window.location.reload();
      })
      .catch((err) => setError(err.response.data.message));
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormFields({ ...formFields, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isLogin) {
      login();
    } else {
      if (formFields.password !== formFields.cpassword) {
        setError("Password confirmation does not matched.");
        return;
      }
      register();
    }
  };

  return (
    <FormContainer>
      <Title>SpendSmart</Title>
      <ImageWrapper>
        <img src={Pic1} alt="Decorative" />
      </ImageWrapper>
      <Tagline>Best way to track your money!</Tagline>
      <FormStyled $isLogin={isLogin} className={isLogin ? "signin" : "signup"} onSubmit={handleSubmit}>
        <h3>{isLogin ? "Sign In" : "Sign Up"}</h3>
        {error && <p className="error">{error}</p>}
        <div className="input-control">
          <input type="text" name="name" placeholder="Name" onChange={handleChange} />
        </div>
        <div className="input-control">
          <input type="email" name="email" placeholder="Email" onChange={handleChange} />
        </div>
        <div className="input-control">
          <input type="password" name="password" placeholder="Password" onChange={handleChange} />
        </div>
        {!isLogin && (
          <div className="input-control">
            <input type="password" name="cpassword" placeholder="Confirm Password" onChange={handleChange} />
          </div>
        )}
        <div className="submit-btn">
          <Button name={isLogin ? "Login" : "Register"} bPad=".8rem 1.6rem" bRad="30px" bg="#222260" color="#fff" />
        </div>
        <p>
          {isLogin ? "Don't have an account? " : "Already have an account? "}
          <span className="link" onClick={togglePage}>{isLogin ? "Sign up." : "Sign in."}</span>
        </p>
      </FormStyled>
    </FormContainer>
  );
};

const FormContainer = styled.div`
  background-color: #F0F2F5;
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;  
  padding: 0 5%;  
`;

const Title = styled.h1`
  color: #222260;
  font-size: 2rem;
  margin-bottom: 20px; // Space between title and image
`;

const ImageWrapper = styled.div`
  flex: 1;  
  max-width: 60%;  
  img {
    width: 100%;
    height: auto;  
  }
`;

const Tagline = styled.p`
  color: #707070;
  font-size: 1.2rem;
  margin-top: 20px; // Space between image and tagline
`;

const FormStyled = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1.5rem;
  background: rgba(252, 246, 249, 0.78);
  box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
  padding: 1.5rem;
  border-radius: 20px;
  font-family: 'Segoe UI', sans-serif;
  margin-top: 20px; // Space between tagline and form
`;

export default Authentication;
