import React, { useState } from "react";
import styled from "styled-components";
import Button from "../Button/Button";
import axios from "axios";
import Cookies from "js-cookie";
import Pic1 from '../assets/pic1.png';

const Authentication = () => {
  const [isLogin, setIsLoginPage] = useState(true);

  const togglePage = () => {
    setIsLoginPage(!isLogin);
    setError("");
  };

  const defaultFormFields = {
    name: "",
    email: "",
    password: "",
    cpassword: "",
  };

  const BASE_URL = "http://localhost:5000/api/v1";

  const [error, setError] = useState("");
  const [formFields, setFormFields] = useState(defaultFormFields);

  const login = () => {
    axios
      .post(`${BASE_URL}/login`, formFields)
      .then((res) => {
        alert(res.data.message);
        Cookies.set("et-auth-name", res.data.currentUser.name, { expires: 7 });
        window.location.reload();
      })
      .catch((err) => {
        setError(err.response.data.message);
      });
  };

  const register = () => {
    axios
      .post(`${BASE_URL}/register`, formFields)
      .then((res) => {
        alert(res.data.message);
        window.location.reload();
      })
      .catch((err) => {
        setError(err.response.data.message);
      });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormFields({ ...formFields, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (isLogin) {
      console.log("Logging in...");
      login();
    } else {
      console.log("Signing up...");

      if (formFields.password !== formFields.cpassword) {
        setError("Password confirmation does not matched.");
        return;
      }

      register();
    }
  };

  return (
    <FormContainer>
      <ImageArea src={Pic1} alt="Decorative image" />
      <FormStyled $isLogin={isLogin} onSubmit={handleSubmit}>
        {isLogin ? <h3>Sign In</h3> : <h3>Sign Up</h3>}
        {error && <p className="error">{error}</p>}
        <div className="input-control">
          {isLogin ? (
            <>
              <input type="email" name="email" placeholder="Email" onChange={handleChange} />
              <input type="password" name="password" placeholder="Password" onChange={handleChange} />
            </>
          ) : (
            <>
              <input type="text" name="name" placeholder="Name" onChange={handleChange} />
              <input type="email" name="email" placeholder="Email" onChange={handleChange} />
              <input type="password" name="password" placeholder="Password" onChange={handleChange} />
              <input type="password" name="cpassword" placeholder="Confirm Password" onChange={handleChange} />
            </>
          )}
        </div>
        <div className="submit-btn">
          <Button name={isLogin ? "Login" : "Register"} />
        </div>
        <p>
          {isLogin ? "Don't have an account?" : "Already have an account?"}{" "}
          <span className="link" onClick={togglePage}>
            {isLogin ? "Sign up." : "Sign in."}
          </span>
        </p>
      </FormStyled>
    </FormContainer>
  );
};

const FormContainer = styled.div`
  background-color: #F0F2F5;
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0 10%;
`;

const ImageArea = styled.img`
  width: 50%;  // Adjust width as needed
  height: auto;  // Maintain aspect ratio
`;

const FormStyled = styled.form`
  width: 300px;
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
  background: #FFFFFF;
  padding: 1.5rem;
  border-radius: 10px;
  box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
`;

export default Authentication;
