import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import io from 'socket.io-client';
import Chart from '../Chart/Chart';
import GaugeChart from 'react-gauge-chart';

const socket = io('http://localhost:3000');

function Dashboard() {
  const [data, setData] = useState([]);
  const [currentDate, setCurrentDate] = useState('');

  useEffect(() => {
    fetch('http://192.168.1.23:3000/get-data')
      .then(response => response.json())
      .then(data => setData(data))
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  useEffect(() => {
    const date = new Date();
    const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
    setCurrentDate(date.toLocaleDateString('en-US', options));
  }, []);

  useEffect(() => {
    socket.on('new-data', (newData) => {
      setData((prevData) => [...prevData, newData]);
    });

    return () => {
      socket.off('new-data');
    };
  }, []);

  const formatDate = (dateString) => {
    const options = {
      timeZone: 'Asia/Manila',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit',
    };
    return new Date(dateString).toLocaleString('en-US', options);
  };

  const latestTemperature = data.length > 0 ? data[data.length - 1].temperature : 0;
  const latestHumidity = data.length > 0 ? data[data.length - 1].humidity : 0;

  const tempGaugeValue = latestTemperature / 50; // Assuming max temperature is 50 degrees Celsius
  const humidityGaugeValue = latestHumidity / 100; // Humidity is a percentage

  return (
    <DashboardStyled>
      <InnerLayout>
        <h2>Dashboard</h2>
        <p>{currentDate}</p>
        <div className="stats-con">
          <div>
            <h2>Temperature</h2>
            <GaugeChart id="temp-gauge" nrOfLevels={30} colors={["#FF5F6D", "#FFC371"]} arcWidth={0.3} percent={tempGaugeValue} />
            <p>{latestTemperature}&deg;C</p>
          </div>
          <div>
            <h2>Humidity</h2>
            <GaugeChart id="humidity-gauge" nrOfLevels={30} colors={["#5BC0EB", "#FDE74C"]} arcWidth={0.3} percent={humidityGaugeValue} />
            <p>{latestHumidity}%</p>
          </div>
        </div>
        <div className="chart-con">
          <h2>Analytics</h2>
          <small>Track your temperature and humidity over time.</small>
          <Chart data={data} />
        </div>
        <div className="history-con">
          <h2>Temperature and Humidity Data</h2>
          <table>
            <thead>
              <tr>
                <th>Temperature (&deg;C)</th>
                <th>Humidity (%)</th>
                <th>Timestamp</th>
              </tr>
            </thead>
            <tbody>
              {data.map((record, index) => (
                <tr key={index}>
                  <td>{record.temperature}</td>
                  <td>{record.humidity}</td>
                  <td>{formatDate(record.time)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </InnerLayout>
    </DashboardStyled>
  );
}

// Styled components remain the same

export default Dashboard;
