import React, { useState, useEffect } from 'react';
import { Chart as ChartJs, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';
import { Line } from 'react-chartjs-2';
import styled from 'styled-components';

ChartJs.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
);

function Chart() {
    const [data, setData] = useState([]);
    const [timeframe, setTimeframe] = useState('monthly');

    useEffect(() => {
        fetch('http://192.168.1.23:3000/get-data')
            .then(response => response.json())
            .then(data => setData(data))
            .catch(error => console.error('Error fetching data:', error));
    }, []);

    const now = new Date();

    const filterItemsByDateRange = (items, startDate, endDate) => {
        return items.filter(item => {
            const itemDate = new Date(item.time);
            return itemDate >= startDate && itemDate <= endDate;
        });
    };

    const aggregateData = (items, period) => {
        const periods = {
            monthly: { start: startOfMonth(now), end: endOfMonth(now) },
            weekly: { start: startOfWeek(now), end: endOfWeek(now) },
            yearly: { start: startOfYear(now), end: endOfYear(now) },
        };

        const { start, end } = periods[period];
        const filteredItems = filterItemsByDateRange(items, start, end);

        return filteredItems.map(item => ({
            ...item,
            formattedDate: format(new Date(item.time), 'dd/MM/yyyy')
        }));
    };

    const aggregatedData = aggregateData(data, timeframe);

    const chartData = {
        labels: aggregatedData.map(item => item.formattedDate),
        datasets: [
            {
                label: 'Temperature',
                data: aggregatedData.map(item => item.temperature),
                borderColor: 'blue',
                backgroundColor: 'rgba(0, 0, 255, 0.5)',
                tension: 0.2,
                fill: true,
            },
            {
                label: 'Humidity',
                data: aggregatedData.map(item => item.humidity),
                borderColor: 'green',
                backgroundColor: 'rgba(0, 255, 0, 0.5)',
                tension: 0.2,
                fill: true,
            }
        ]
    };

    const options = {
        responsive: true,
        plugins: {
            legend: {
                position: 'bottom',
                labels: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            },
            tooltip: {
                mode: 'index',
                intersect: false,
                bodyFont: {
                    family: 'Segoe UI',
                },
                titleFont: {
                    family: 'Segoe UI',
                },
            },
        },
        scales: {
            x: {
                display: true,
                title: {
                    display: false,
                },
                ticks: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            },
            y: {
                display: true,
                title: {
                    display: false,
                },
                beginAtZero: true,
                ticks: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            }
        }
    };

    return (
        <ChartStyled>
            <div className="header">
                <div className="timeframe-selector">
                    <label htmlFor="timeframe">Select Timeframe: </label>
                    <select id="timeframe" value={timeframe} onChange={(e) => setTimeframe(e.target.value)}>
                        <option value="weekly">Weekly</option>
                        <option value="monthly">Monthly</option>
                        <option value="yearly">Yearly</option>
                    </select>
                </div>
            </div>
            <Line options={options} data={chartData} />
        </ChartStyled>
    );
}

const ChartStyled = styled.div`
    background: #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0);
    padding: 1rem;
    border-radius: 20px;
    height: 100%;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;

    .header {
        display: flex;
        justify-content: flex-end;
    }

    .timeframe-selector {
        display: flex;
        align-items: center;
        margin-bottom: 0.5rem;

        label {
            margin-right: 1rem;
            font-size: 1rem;
            color: #696969;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
        }

        select {
            padding: 0.5rem;
            border: 1px solid #ddd;
            border-radius: 5px;
            font-size: 1rem;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            color: #696969;
        }
    }
`;

export default Chart;
