import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';

function ViewTransaction() {
    const { incomes, expenses } = useGlobalContext();
    const [data, setData] = useState([]);

    useEffect(() => {
        fetch('http://192.168.1.23:3000/get-data')
            .then(response => response.json())
            .then(data => setData(data))
            .catch(error => console.error('Error fetching data:', error));
    }, []);

    // Combine incomes and expenses into a single transaction history array
    const history = [...incomes, ...expenses].sort((a, b) => new Date(b.date) - new Date(a.date));

    return (
        <ViewTransactionStyled>
            <h2>Transaction History</h2>
            <div className="table-responsive">
                <table>
                    <thead>
                        <tr>
                            <th>Category</th>
                            <th>Date</th>
                            <th>Description</th>
                            <th>Amount</th>
                        </tr>
                    </thead>
                    <tbody>
                        {history.map((item) => (
                            <tr key={item._id}>
                                <td>
                                    <span className="icon">{getIcon(item.category, item.type)}</span>
                                    {item.category}
                                </td>
                                <td>{new Date(item.date).toLocaleDateString()}</td>
                                <td>{item.description}</td>
                                <td className={`amount ${item.type === 'expense' ? 'negative' : 'positive'}`}>
                                    ${item.type === 'expense' ? '-' : '+'}{Math.abs(item.amount)}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <h2>Temperature and Humidity Data</h2>
            <div className="history-con">
                <table>
                    <thead>
                        <tr>
                            <th>Temperature (&deg;C)</th>
                            <th>Humidity (%)</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map((record, index) => (
                            <tr key={index}>
                                <td>{record.temperature}</td>
                                <td>{record.humidity}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </ViewTransactionStyled>
    );
}

const ViewTransactionStyled = styled.div`
    background-color: #ffffff; 
    padding: 20px;
    border-radius: 8px;
    font-family: 'Segoe UI', sans-serif;

    h2 {
        margin-top: 1rem;
        margin-left: 1rem;
        color: #222260;
        margin-bottom: 16px;
        font-size: 1.5rem;
    }

    .table-responsive {
        overflow-x: auto;
    }

    table {
        width: 100%;
        border-collapse: collapse;

        th, td {
            padding: 12px 16px;
            text-align: left;
            font-family: 'Segoe UI', sans-serif;
            border: none; /* Remove borders */
        }

        thead th {
            color: #222260;
            font-weight: bold;
            padding: 10px;
        }

        tr:last-child td {
            border-bottom: none;
        }

        .icon {
            font-size: 20px;
            margin-right: 8px;
            vertical-align: middle;
        }

        .amount {
            font-weight: bold;
            &.positive {
                color: green;
            }
            &.negative {
                color: red;
            }
        }
    }

    .history-con {
        background: #ffffff;
        border-radius: 8px;
        box-shadow: 0 4px 8px rgba(0, 0, 0, 0.08);
        padding: 20px;
        width: 100%;
        margin-top: 2rem;

        table {
            width: 100%;
            border-collapse: collapse;
            margin-top: 1rem;
        }

        th, td {
            padding: 12px;
            text-align: left;
            border-bottom: 1px solid #ddd;
        }

        th {
            background-color: #f2f2f2;
            font-weight: bold;
        }

        tr:hover {
            background-color: #f1f1f1;
        }
    }
`;

export default ViewTransaction;
