import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

function Dashboard() {
  const [data, setData] = useState([]);
  const [currentDate, setCurrentDate] = useState('');

  useEffect(() => {
    fetch('http://192.168.1.23:3000/get-data')
      .then(response => response.json())
      .then(data => setData(data))
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  useEffect(() => {
    const date = new Date();
    const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
    setCurrentDate(date.toLocaleDateString('en-US', options));
  }, []);

  const latestTemperature = data.length > 0 ? data[data.length - 1].temperature : '-';
  const latestHumidity = data.length > 0 ? data[data.length - 1].humidity : '-';

  return (
    <div className="dashboard">
      <div className="inner-layout">
        <h2>Dashboard</h2>
        <p>{currentDate}</p> 
        <div className="stats-con">
          <div className="stat-box">
            <h2>Temperature</h2>
            <p>{latestTemperature}&deg;C</p>
          </div>
          <div className="stat-box">
            <h2>Humidity</h2>
            <p>{latestHumidity}%</p>
          </div>
        </div>
        <div className="history-con">
          <h2>Temperature and Humidity Data</h2>
          <table>
            <thead>
              <tr>
                <th>Temperature (&deg;C)</th>
                <th>Humidity (%)</th>
                <th>Time</th>
              </tr>
            </thead>
            <tbody>
              {data.map((record) => (
                <tr key={record._id}>
                  <td>{record.temperature}</td>
                  <td>{record.humidity}</td>
                  <td>{new Date(record.time).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

const DashboardStyled = styled.div`
  font-family: 'Segoe UI', sans-serif; 
  background-color: #ffffff; 
  padding: 20px;

  .stats-con {
    margin-top: 1rem;
    display: grid;
    grid-template-columns: repeat(2, 1fr); 
    grid-gap: 1rem; 
    margin-bottom: 1rem; 

    > div {
      background: #ffffff; 
      border-radius: 8px; 
      box-shadow: 0 4px 8px rgba(0,0,0,0.08); 
      padding: 20px; 
      display: flex;
      flex-direction: column;
      justify-content: space-between;
      margin: 1rem 0;

      h2 {
        font-size: 1.2rem;
        color: #222260;
        margin-bottom: 0.5rem;
      }
      p {
        font-size: 2.4rem;
        font-weight: bold;
        margin: 0;
      }
      small {
        font-size: 1rem;
        color: #666;
      }
    }
  }

  .history-con {
    background: #ffffff;
    border-radius: 8px;
    box-shadow: 0 4px 8px rgba(0,0,0,0.08);
    padding: 20px;
    width: 100%;
    margin-top: 2rem;

    h2 {
      font-size: 1.5rem;
      margin-bottom: 1rem;
    }

    table {
      width: 100%;
      border-collapse: collapse;
      margin-top: 1rem;
    }

    th, td {
      padding: 12px;
      text-align: left;
      border-bottom: 1px solid #ddd;
    }

    th {
      background-color: #f2f2f2;
      font-weight: bold;
    }

    tr:hover {
      background-color: #f1f1f1;
    }
  }
`;

const InnerLayout = styled.div`
  padding: 2rem;
`;

export default Dashboard;
